    /*
     * Deleting t_hebergement
     */
    function remove($id)
    {
        $t_hebergement = $this->Hebergement_model->get_t_hebergement($id);

        // check if the t_hebergement exists before trying to delete it
        if(isset($t_hebergement['id']))
        {
            $this->Hebergement_model->delete_t_hebergement($id);
            redirect('hebergement');
