class Theme extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Theme_model');
    } 

    /*
     * Listing of t_theme
     */
    function index()
    {
        $data['t_theme'] = $this->Theme_model->get_all_t_theme();
        
        $res = array();
        foreach($data['t_theme'] as $row) {
			$element = new stdClass();
            $element->id = $row['id'];
            $element->name =  $row['name'];
                      
            $this->load->model('Domaine_theme_ip_model');
            $domaine_ip = $this->Domaine_theme_ip_model->get_t_domaine_theme_ip_by_theme($row['id']);   
        
            $element->nb_site = ($domaine_ip != null && count($domaine_ip) >0 ) ? count($domaine_ip) : 0;           
           
            $res[] = $element;	
        }
        $data['nb_theme'] = ($data['t_theme'] != null && count($data['t_theme']) >0 ) ? count($data['t_theme']) : 0;
 
        $data['t_theme'] = $res;  
        $data['_view'] = 'theme/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new t_theme
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'name' => $this->input->post('name'),
            );
            
            $t_theme_id = $this->Theme_model->add_t_theme($params);
            redirect('theme/index');
        }
        else
        {            
            $data['_view'] = 'theme/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a t_theme
     */
    function edit($id)
    {   
        // check if the t_theme exists before trying to edit it
        $data['t_theme'] = $this->Theme_model->get_t_theme($id);
        
        if(isset($data['t_theme']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'name' => $this->input->post('name'),
                );

                $this->Theme_model->update_t_theme($id,$params);            
                redirect('theme/index');
            }
            else
            {
                $data['_view'] = 'theme/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The t_theme you are trying to edit does not exist.');
    } 

    /*
     * Deleting t_theme
     */
    function remove($id)
    {
        $t_theme = $this->Theme_model->get_t_theme($id);

        // check if the t_theme exists before trying to delete it
        if(isset($t_theme['id']))
        {
            $this->Theme_model->delete_t_theme($id);
            redirect('theme/index');
        }
        else
            show_error('The t_theme you are trying to delete does not exist.');
    }
    
}
